let css,id;async function refresh_css(){const response=await fetch("填入你的css链接");if(!response.ok)return void toastr.error("未能从 '填入你的css链接' 获取 css 文件, 请确认链接是否有效","实时修改css");const new_css=await response.text();css!=new_css&&(css=new_css,$("#customCSS").val(css)[0].dispatchEvent(new Event("input")))}$((()=>{id=setInterval(refresh_css,1e3)})),$(window).on("unload",(()=>{clearInterval(id)}));